import {db} from '../middlewares/firebase'
import { useEffect, useState } from 'react';
import {collection,getDocs} from "firebase/firestore";


function Dbread(){
    const [data, setData] = useState([]);
    const dataCollection = collection(db,'new')
    useEffect(()=>{

      const getData = async ()=>{
        const data = await getDocs(dataCollection)
        setData(data.docs.map((doc)=>({...doc.data(), id: doc.id})))
      }
      getData()
    }, [])

    return (
      <div className='dataTable'>
        {data.map((items)=>{
          return(
            <div>
              <h1>completed: {items.completed}</h1>
              <h1>completed: {items.description}</h1>
            </div>
          )
        })}
      </div>
    )
}

export default Dbread
