import {db} from '../middlewares/firebase'
import { useState } from 'react';
import {doc, collection, onSnapshot, addDoc, query, orderBy, deleteDoc, setDoc} from "firebase/firestore";

const firestoreDb = {
  dbcreate: async (data,transid) => {
    try {
      await addDoc(collection(db, 'new'), {
        playerId: data.playerid,
        bankAccount: data.bankacc,
        bankName: data.bankName,
        transId: transid,
        withdrawid: data.withdrawId,
        mess: data.mess,
        ammount: data.ammount,
        created: Timestamp.now()
      })
    } catch (err) {
      alert(err)
    }
  },
  dbread: ()=>{
    const [input, setInput] = useState("")
  
    //GETTINGS LISTS
    const [lists, setLists] = useState([])
    useEffect(()=> {
      const q = query(collection(db, "new"),  orderBy("timestamp", "desc"));
  
      const unsubscribe = onSnapshot(q, (snapshot) => {
        setLists(snapshot.docs.map(doc => ({...doc.data(), id: doc.id})))
        setInput("")
      });
        return () => unsubscribe()
   }, [])   
  }
}
export default firestoreDb
