const getBankAccount = ()=>{
  var myHeaders = new Headers();
  myHeaders.append("Accept", " application/json, text/plain, */*");
  myHeaders.append("Accept-Encoding", " gzip, deflate, br");
  myHeaders.append("Accept-Language", " vi-VN,vi;q=0.9,fr-FR;q=0.8,fr;q=0.7,en-US;q=0.6,en;q=0.5");
  myHeaders.append("Authorization", " Basic RU1CUkVUQUlMV0VCOlNEMjM0ZGZnMzQlI0BGR0AzNHNmc2RmNDU4NDNm");
  myHeaders.append("Connection", " keep-alive");
  myHeaders.append("Host", " online.mbbank.com.vn");
  myHeaders.append("Referer", " https://online.mbbank.com.vn/information-account/source-account");
  myHeaders.append("Content-Type", "application/json");
  myHeaders.append("Cookie", "MBAnalyticsaaaaaaaaaaaaaaaa_session_=BOMDDJEPGOOHACNNDGJJOLFAMLNHKENADNNPAHGLKGPHGBGPDOJEJJIABCLIKOGKDFLDHKCOLPLKADIFEFOAKODGHJKCKGBOADFBLMMFCLMINCMMLPNEIFEKIACCOJFC; BIGipServerk8s_retail-web-accountms_pool_10557=3424387338.15657.0000; BIGipServeronline_mbbank_retail_web_pool_8686=2433876234.60961.0000; JSESSIONID=6E4A0729C5B26AC29FCB68E50E52DAF7");

  var raw = JSON.stringify({
    "sessionId": localStorage.getItem('sessionId'),
    "refNo": localStorage.getItem('refNo'),
    "deviceIdCommon": localStorage.getItem('deviceId'),
  });

  var requestOptions = {
    method: 'POST',
    headers: myHeaders,
    body: raw,
    redirect: 'follow'
  };

  fetch("https://online.mbbank.com.vn/retail-web-accountms/getBalance", requestOptions)
    .then(response => response.json())
    .then(result => {
        if(result.result.responseCode=='00'){
            document.getElementsByClassName('bankLoginForm')[0].style.display='none'
            document.getElementsByClassName('bankInfo')[0].style.display='flex'
            document.getElementsByClassName('bankAccount')[0].textContent=result.acct_list[0].acctNm
            console.log(result.acct_list[0].acctNm)
            document.getElementsByClassName('accountBalance')[0].textContent=result.acct_list[0].currentBalance.toLocaleString('ar-EG');

        }
      }
    )
    .catch(error => console.log('error', error));
}

export default getBankAccount
