import React, {Component} from 'react';
import Bill from './bill.generator';
import waitForElm from '../middlewares/waitForElm';
import HistorySearch from './history.formField';
import dateValue from "../constant/date"

class Billing extends Component {
  render() {
      return(
          <div className="wrapper">
              <div className="bill-container">
                  {/* Logo & Date */}
                  <div className="head-bill">
                      <div className="logo">
                          <img src={logoVietin}></img>
                      </div>
                      <div className="time-code">
                          <div className="time">
                              <p className="date">20/10/2022</p>
                              &nbsp;
                              <p className="hour">18:44</p>
                          </div>
                          <div className="code">11nWo-7d9YEsteD</div>
                      </div>
                  </div>
                  {/* Status */}
                  <div className="status">
                      <div className="status-content">Quý khách đã giao dịch thành công!</div>
                  </div>
                  {/* Infomation */}
                  <div className="information">
                      <div className="info">
                          <div className="left-info">Từ tài khoản</div>
                          <div className="right-info">
                              <div className="above-content">********4484</div>
                              <div className="below-content">TRAN THI ANH TUYET</div>
                          </div>
                      </div>
                      <div className="info">
                          <div className="left-info">Đến tài khoản</div>
                          <div className="right-info account">
                              <div className="above-content">100872860817</div>
                              <div className="below-content">CAO TAI PHAT</div>
                          </div>
                      </div>
                      <div className="info">
                          <div className="left-info">Số tiền</div>
                          <div className="right-info fee">
                              <div className="above-content">100,000 VND</div>
                              <div className="below-content">Một trăm nghìn đồng</div>
                          </div>
                      </div>
                      <div className="info a-line">
                          <div className="left-info">Phí</div>
                          <div className="right-info">
                              <div className="above-content">Miễn phí</div>
                          </div>
                      </div>
                      <div className="info a-line">
                          <div className="left-info">Nội dung</div>
                          <div className="right-info">
                              <div className="above-content">CAO TAI PHAT</div>
                          </div>
                      </div>
                  </div>
                  {/* Banner */}
                  <div className="banner">
                      <img src={bannerVietin}></img>
                  </div>
              </div>
          </div>
      )
  }
}

waitForElm('#startTime').then(()=>{
  document.getElementById('startTime').value = new Date(dateValue.currentStartDay).getFullYear()+"-"+(new Date(dateValue.currentStartDay).getMonth()+1)+'-'+(new Date(dateValue.currentStartDay).getDate())+'T00:00:00'
  document.getElementById('endTime').value = new Date(dateValue.currentEndDay).getFullYear()+"-"+(new Date(dateValue.currentStartDay).getMonth()+1)+'-'+(new Date(dateValue.currentStartDay).getDate())+'T23:59:59'
})

waitForElm('.billPrinter').then(()=>{
  let billingContent = document.getElementsByClassName('bills-container')[0]
  document.getElementsByClassName('cancelPrnt')[0].addEventListener('click',function(){
    console.log('center')
    billingContent.style.opacity='0'
    billingContent.style.zIndex='-1'
  })
})

waitForElm('.transHistory').then(()=>{
  function downloadCSVFile(csv, filename) {
    var csv_file, download_link;
    csv_file = new Blob([csv], {type: "text/csv"});
    download_link = document.createElement("a");
    download_link.download = filename;
    download_link.href = window.URL.createObjectURL(csv_file);
    download_link.style.display = "none";
    document.body.appendChild(download_link);
    download_link.click();
  }
  document.getElementById("download-button").addEventListener("click", function () {
    var html = document.getElementsByClassName("tableData")[0].outerHTML;
    htmlToCSV(html, "TaiBieu.csv");
  });


  function htmlToCSV(html, filename) {
    var data = [];
    var rows = document.querySelectorAll("table tr");					
    for (var i = 0; i < rows.length; i++) {
      var row = [], cols = rows[i].querySelectorAll("td, th");						
      for (var j = 0; j < cols.length; j++) {
        row.push(cols[j].innerText);
      }
      data.push(row.join(","));		
    }
    downloadCSVFile(data.join("\n"), filename);
  }
})

export default Billing;
