import React from 'react'
import vcbLogo from '../asset/media/images/brands/logo/vcb.webp'
import bidvLogo from '../asset/media/images/brands/logo/bidv.png'
import viettinLogo from '../asset/media/images/brands/logo/viettin.png'
import waitForElm from '../middlewares/waitForElm'
import bankLogin from '../api/bankLogin'
import { useEffect, useState } from 'react'

const Sidebar = ()=>{
  const [user, setUser] = useState(0)
  setTimeout(function(){
    setUser(localStorage.getItem('bouser'))
  },1000)
  useEffect(() => {
    document.getElementsByClassName('loginContent')[0].textContent = localStorage.getItem('deviceName')
    document.getElementsByClassName('loginContent')[2].textContent = localStorage.getItem('bankUsername')
    document.getElementsByClassName('loginContent')[3].textContent = localStorage.getItem('bankAccount')
    let requestOptions = {
      method: 'POST',
      redirect: 'follow'
    };
    document.getElementsByClassName('tableOfInfo')[0].addEventListener('click',function(){
      fetch("http://localhost:8090/TotalControl/v2/devices/"+localStorage.getItem('deviceName')+"/sendAai?token="+localStorage.getItem('bankToken')+"&params={query:\"R:.account_no\",action:\"getText\"}", requestOptions)
      .then(response => response.json())
      .then(result => {
        if(!result.value.retval){
          alert('Vui lòng vào mục thông tin của Ngân hàng để lấy thông tin!')
        }else{
          localStorage.setItem('bankAccount',result.value.retval)
          fetch("http://localhost:8090/TotalControl/v2/devices/"+localStorage.getItem('deviceName')+"/sendAai?token="+localStorage.getItem('bankToken')+"&params={query:\"R:.username\",action:\"getText\"}", requestOptions)
          .then(response => response.json())
          .then(result => {
            if(!result.value.retval){
              alert('Vui lòng vào mục thông tin của Ngân hàng để lấy thông tin!')
            }else{
              localStorage.setItem('bankUsername',result.value.retval)
              setTimeout(function(){
                window.location.reload()
              },500)
            }
          }).catch(error => console.log('error', error));
        }
      }).catch(error => console.log('error', error));
    })
  },[]);

    return (
      <div className='sidebar'>
        <div className='sidebarContent'>
          <div className='bankLogin'>
            <div className='bankInfo'>
              <div className='bankLogo'></div>
              <div className='bankAccount'>
                <h2>{user}</h2>
                <table className='tableOfInfo' cellSpacing={0}>
                  <tbody>
                    <tr>
                      <td className='title'>Device:</td>
                      <td className='loginContent'>device@1798187813</td>
                    </tr>
                    <tr>
                      <td className='title'>Ngân hàng:</td>
                      <td className='loginContent'>Vietin</td>
                    </tr>
                    <tr>
                      <td className='title'>Thẻ Xuất:</td>
                      <td className='loginContent'>103876875487</td>
                    </tr>
                  </tbody>
                </table>
              </div>
              <div className='bank'></div>
            </div>
            <button className='logout'>Log Out</button>
