const page1 = (data)=>{
    let token = localStorage.getItem('bankToken')
    let device = localStorage.getItem('deviceName')
    let requestOptions = {
        method: 'POST',
        redirect: 'follow'
      };
      
      fetch("http://localhost:8090/TotalControl/v2/devices/"+device+"/screen/texts?token="+token+"&text="+data.ammount+"&position=0", requestOptions)
        .then(response => response.json())
        .then(result => {
          let requestOptions = {
              method: 'POST',
              redirect: 'follow'
            };             
            fetch("http://localhost:8090/TotalControl/v2/devices/"+device+"/screen/texts?token="+token+"&text="+data.mess+"&position=1", requestOptions)
              .then(response => response.json())
              .then(result => {
                  let requestOptions = {
                    method: 'POST',
                    redirect: 'follow'
                  };
                  fetch("http://localhost:8090/TotalControl/v2/devices/"+device+"/sendAai?token="+token+"&params={query:'T:Chọn tài khoản',action:\"click\"}", requestOptions)
                    .then(response => response.text())
                    .then(result => {
                      let requestOptions = {
                        method: 'POST',
                        redirect: 'follow'
                      };
                      setTimeout(function(){
                        fetch("http://localhost:8090/TotalControl/v2/devices/"+device+"/screen/inputs?token="+token+"&code=recentapp&state=press", requestOptions)
                        .then(response => response.json())
                        .then(result => {
                          console.log(result)
                          fetch("http://localhost:8090/TotalControl/v2/devices/"+device+"/screen/inputs?token="+token+"&code=recentapp&state=press", requestOptions)
                          .then(response => response.json())
                          .then(result => {
                            setBankAcc(device,token,data)
                          }).catch(error => console.log('error', error));
                        }).catch(error => console.log('error', error));
                      },1000)
                    }).catch(error => console.log('error', error));
              }).catch(error => console.log('error', error));
        }).catch(error => console.log('error', error));
        //Set amount and message
}

function setBankAcc(device,token,data){
    let requestOptions = {
      method: 'POST',
      redirect: 'follow'
    };
    fetch("http://localhost:8090/TotalControl/v2/devices/"+device+"/sendAai?token="+token+"&params={query:'T:Nhập số TK/Tên chủ TK thụ hưởng',action:\"setText('"+data.bankAcc+"')\"}", requestOptions)
    .then(response => response.json())
    .then(result => {
      console.log(!result.value.retval)
      if(!result.value.retval){
        setBankAcc(device,token,data)
      }else{
        setBankText(device,token,data)
      }
      // console.log(result)
    }).catch(error => console.log('error', error));
}

function setBankText(device,token,data){
  let requestOptions = {
    method: 'POST',
    redirect: 'follow'
  };
  fetch("http://localhost:8090/TotalControl/v2/devices/"+device+"/sendAai?token="+token+"&params={query:'T:Ngân hàng thụ hưởng',action:\"click\"}", requestOptions)
    .then(response => response.json())
    .then(result => {
      fetch("http://localhost:8090/TotalControl/v2/devices/"+device+"/sendAai?token="+token+"&params={query:'T:Nhập số TK/Tên chủ TK thụ hưởng',action:\"setText('"+data.bankAcc+"')\"}", requestOptions)
      .then(response => response.json())
      .then(result => {
        console.log(!result.value.retval)
        if(!result.value.retval){
          setBankAcc(device,token,data)
        }else{
          setBankText(device,token,data)
        }
        // console.log(result)
      }).catch(error => console.log('error', error));
    }).catch(error => {console.log(error)});
}

export default page1
