import {db} from '../middlewares/firebase'
import { useEffect, useState } from 'react';
import {collection,getDocs} from "firebase/firestore";



function Dbread(){
    const [data, setData] = useState([]);
    const dataCollection = collection(db,'transfersHistory')
    useEffect(()=>{

      const getData = async ()=>{
        const data = await getDocs(dataCollection)
        setData(data.docs.map((doc)=>({...doc.data(), id: doc.id})))
      }
      getData()
    }, [])

    return (
      <table className='transHistory' cellSpacing={0}>
        <thead>
          <th>Player Id</th>
          <th>Số Tài Khoản</th>
          <th>Ngân Hàng</th>
          <th>Số Tiền</th>
          <th>Tên Khách Hàng</th>
          <th>Mã Xuất Khoản</th>
          <th>Mã Giao Dịch</th>
          <th>Tạo Hóa Đơn</th>
          <th></th>
        </thead>
        {data.map((items)=>{
          return(

              <tbody>
                <tr>
                  <td>{items.playerId}</td>
                  <td>{items.bankAccount}</td>
                  <td>{items.bankName}</td>
                  <td>{items.ammount}</td>
                  <td>{items.mess}</td>
                  <td>{items.withdrawid}</td>
                  <td>{items.transId}</td>
                  <td><button className='billPrinter fa-solid fa-print' data-id={JSON.stringify(items)}></button></td>
                </tr>
              </tbody>
          
          )
        })}
      </table>
    )
}

export default Dbread
