  var input = document.getElementById('otp')
  input.addEventListener("keypress", function(event) {
    if (event.key == "Enter") {
      document.getElementById("submitOtp").click();
    }
  });
  document.getElementById("submitOtp").addEventListener('click',()=>{
    console.log(document.getElementById('otp').value)
  })
})

export default Transitions;
