import {db} from '../middlewares/firebase'
import {collection, addDoc, Timestamp} from 'firebase/firestore'

const firestoreDb = {
  dbcreate: async () => {
    try {
      await addDoc(collection(db, 'new'), {
        title: 'b',
        description: 'c',
        completed: 'd',
        created: Timestamp.now()
      })
    } catch (err) {
      alert(err)
    }
  }
}
export default firestoreDb
