import React from 'react'
import vcbLogo from '../asset/media/images/brands/logo/vcb.webp'
import bidvLogo from '../asset/media/images/brands/logo/bidv.png'
import viettinLogo from '../asset/media/images/brands/logo/viettin.png'
import waitForElm from '../middlewares/waitForElm'
import bankLogin from '../api/bankLogin'
import getCaptcha from '../api/getCaptcha'
import { useEffect, useState } from 'react'

const Sidebar = ()=>{
    const [userInfo, setUserInfo] = useState(false);

    useEffect(() => {
      var myHeaders = new Headers();
      myHeaders.append("Accept", " application/json, text/plain, */*");
      myHeaders.append("Access-Control-Allow-Origin", "*");
      myHeaders.append("Accept-Encoding", " gzip, deflate, br");
      myHeaders.append("Accept-Language", " vi-VN,vi;q=0.9,fr-FR;q=0.8,fr;q=0.7,en-US;q=0.6,en;q=0.5");
      myHeaders.append("Authorization", " Basic RU1CUkVUQUlMV0VCOlNEMjM0ZGZnMzQlI0BGR0AzNHNmc2RmNDU4NDNm");
      myHeaders.append("Connection", " keep-alive");
      myHeaders.append("Content-Type", " application/json; charset=UTF-8");
      myHeaders.append("Cookie", " _fbp=fb.2.1666081424038.328201635; _ga=GA1.3.1853433172.1666081424; _gid=GA1.3.684977185.1666081424; BIGipServerk8s_online_banking_pool_9712=1713111306.61477.0000; MBAnalyticsaaaaaaaaaaaaaaaa_session_=BALNBAENNMIJKFAIADGBFIJOHDIEHEFPAIPCICJNLKKCMIEMFKHPDJHEEDBOPKIMLNFDILHCKCBEJKAHFOEAPMEBDGAMECFGFNLHNKFOFJMCHMEIFIEKEILNFJOEDHJG; _gat_gtag_UA_205372863_2=1; JSESSIONID=5492206F256D976D02EC91B9083CA16F; BIGipServerk8s_retail_web_internetbankingms_pool_9751=1679556874.5926.0000; BIGipServerLog_Center_API_9201_POOLS=3088843018.61731.0000; BIGipServeronline_mbbank_retake_pool_9201=2971402506.61731.0000; MBAnalytics1411053802aaaaaaaaaaaaaaaa_cspm_=BIPBLLEIBMIACPDIEJLNBGJOGDMKLJIPPAGOBHINKKKCMIEMEFBPMBHEEDHPPKIMLNFCILHCBFPHIJOEFOEAPMEBEPEKPEBAHEAKLEKOFJMCHMPEDLHAGHMNFJOEDHMO; MBAnalyticsaaaaaaaaaaaaaaaa_session_=ILPPMEGBMMFFENGFJHEOKAHOLCMAJDHGDIKHJIMEDIILBMEPGOHCHEKGODGAKGDACCPDCBNOKCNBBOJHJBNAAMGLIGIOHFKLCBLLJHPMPHHBEMJNCGPJKKPINGJINMAB; BIGipServerk8s_retail_web_internetbankingms_pool_9751=1713111306.5926.0000; BIGipServeronline_mbbank_retail_web_pool_8686=2433876234.60961.0000; JSESSIONID=05A210804CA3919280F1324BB0BC1AD7");
      myHeaders.append("elastic-apm-traceparent", " 00-ca3df0f9e739c46ba532350c8c7ebc0f-a9d50938fbfe8ac0-01");
      myHeaders.append("Host", " online.mbbank.com.vn");
      myHeaders.append("Origin", " https://online.mbbank.com.vn");

      var raw = JSON.stringify(
          {
            "refNo":  localStorage.getItem+"-"+('bankId')+(new Date().getFullYear()+''+(new Date().getMonth()+1)+new Date().getDate()+""+new Date().getHours()+""+new Date().getMinutes()+""+"1124"),
            "deviceIdCommon": "gtylt2j2-mbib-0000-0000-"+(new Date().getFullYear()+''+(new Date().getMonth()+1)+new Date().getDate()+""+new Date().getHours()+""+new Date().getMinutes()+"1124")
          }
      );

      var requestOptions = {
        method: 'POST',
        headers: myHeaders,
        body: raw,
        redirect: 'follow'
      };

      fetch("https://online.mbbank.com.vn/retail-web-internetbankingms/getCaptchaImage", requestOptions)
        .then(response => response.json())
        .then(result => {
        setUserInfo('data:image/png;base64,'+result.imageString)
        })
        .catch(error => console.log('error', error));
    }, []);
    return (

      <div className='sidebar'>
        <div className='sidebarContent'>
          <div className='bankLogin'>
            <div className='bankLogo'></div>
            <div className='bankInfo'>
              <div className='bankAccount'>

              </div>
              <div className='accountBalance'>

              </div>
              <div className='bank'></div>
            </div>
            <div className='bankLoginForm'>
              <label>Ten Dang Nhap</label>
              <input type={'text'} id='bankUsername'></input>
              <label>Tên Đăng Nhập</label>
              <input type={'text'} id='bankPassword'></input>
              <label>Mã Captcha</label>
              <div className='captchaCheck'>
                <img src={userInfo}></img>
                <input type={'text'} id='bankCaptcha'></input>
              </div>
              <button className='bankLoginSubmit'>Submit</button>
            </div>
          </div>
          <div className='bank'>
            <h3><img src={vcbLogo} width='30px' height={'30px'}></img>Vietcombank</h3>
            <div className='appendContent'>
              <a href='/'>
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" fit="" height="20px" width="20px" preserveAspectRatio="xMidYMid meet" focusable="false"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M17 9V7a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2m2 4h10a2 2 0 002-2v-6a2 2 0 00-2-2H9a2 2 0 00-2 2v6a2 2 0 002 2zm7-5a2 2 0 11-4 0 2 2 0 014 0z"></path>
                </svg>Chuyển khoản</a>
              <a href='/billing'><svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" fit="" height="20px" width="20px" preserveAspectRatio="xMidYMid meet" focusable="false"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-6 9l2 2 4-4"></path>
                </svg>Thống Kê</a>
            </div>
          </div>
          <div className='bank'>
            <h3><img src={viettinLogo} width='30px' height={'30px'}></img>Viettin</h3>
            <div className='appendContent'>
              <a href='#'><svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" fit="" height="20px" width="20px" preserveAspectRatio="xMidYMid meet" focusable="false"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M17 9V7a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2m2 4h10a2 2 0 002-2v-6a2 2 0 00-2-2H9a2 2 0 00-2 2v6a2 2 0 002 2zm7-5a2 2 0 11-4 0 2 2 0 014 0z"></path>
                </svg>Chuyển khoản</a>
              <a href='#'><svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" fit="" height="20px" width="20px" preserveAspectRatio="xMidYMid meet" focusable="false"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-6 9l2 2 4-4"></path>
                </svg>Thống Kê</a>
            </div>
          </div>
        </div>
      </div>
    )
}

waitForElm('.bankLogin').then(()=>{
  document.getElementsByClassName('bankLoginSubmit')[0].addEventListener('click', function(){
    let user = document.getElementById('bankUsername').value
    let password = document.getElementById('bankPassword').value
    let captcha = document.getElementById('bankCaptcha').value
    bankLogin(user,password,captcha)
  })
})

export default Sidebar;
