import {db} from '../middlewares/firebase'
import { useState } from 'react';
import {doc, collection, onSnapshot, addDoc, query, orderBy, deleteDoc, setDoc, Timestamp} from "firebase/firestore";

const firestoreDb = {
  dbcreate: async (result,transid) => {
    try {
      await addDoc(collection(db, 'transfersHistory'), {
        playerId: result.playerId,
        bankAccount: result.bankAcc,
        bankName: result.bankName,
        transId: transid,
        withdrawid: result.withdrawid,
        mess: result.mess,
        ammount: result.ammount,
        created: Timestamp.now()
      })
    } catch (err) {
      console.log(err)
    }
  }
}

export default firestoreDb
