const getDevice = ()=>{
    var token = localStorage.getItem('bankToken')
    var requestOptions = {
        method: 'GET',
        redirect: 'follow'
      };

      fetch("http://localhost:8090/TotalControl/v2/devices/main?token="+token, requestOptions)
        .then(response => response.json())
        .then(result => {
            console.log(result.id)
            if(!result.id){
                document.getElementsByClassName('bankLoginForm')[0].style.display="flex"
                document.getElementsByClassName('bankAccount')[0].style.display='none'
                console.log('Vui lòng đăng nhập')
            }else{
                localStorage.setItem('deviceName',result.id)
            }
        }).catch(error => {
            document.getElementsByClassName('bankLoginForm')[0].style.display="flex"
            document.getElementsByClassName('bankAccount')[0].style.display='none'
            alert('Vui lòng kết nối Total')
        });
}

export default getDevice
