const getBankAccount = ()=>{
  let userInfo = JSON.parse(localStorage.getItem('userInfo'))
  let myHeaders = new Headers();
  myHeaders.append("Accept", " application/json");
  myHeaders.append("Accept-Encoding", " gzip, deflate, br");
  myHeaders.append("Accept-Language", " vi");
  myHeaders.append("Authorization", " Bearer null");
  myHeaders.append("Connection", " keep-alive");
  myHeaders.append("Content-Type", " application/json");
  myHeaders.append("Host", " digiapp.vietcombank.com.vn");
  myHeaders.append("Origin", " https://vcbdigibank.vietcombank.com.vn");
  myHeaders.append("Referer", " https://vcbdigibank.vietcombank.com.vn/");
  myHeaders.append("X-Channel", " Web");
  myHeaders.append("X-Request-ID", " 1665804102772");
  myHeaders.append("Cookie", "TS01388157=011fc56c769f04e6755078ad2b2694300448ff3979e3d70665ecc5e4d86c356330f0bd67089c64af68d067c002cff64274c92c1936");
  
  let raw = JSON.stringify({
    "DT": "Windows",
    "PM": "Chrome 106.0.0.0",
    "OV": "10",
    "lang": "vi",
    "accountNo": userInfo.defaultAccount,
    "accountType": "D",
    "mid": 13,
    "cif": userInfo.cif,
    "user": userInfo.username,
    "mobileId": userInfo.mobileId,
    "clientId": userInfo.clientId,
    "sessionId": localStorage.getItem('sessionId')
  });
  
  let requestOptions = {
    method: 'POST',
    headers: myHeaders,
    body: raw,
    redirect: 'follow'
  };
  
  fetch("https://digiapp.vietcombank.com.vn/bank-service/v1/get-account-detail", requestOptions)
    .then(response => response.json())
    .then(result => {
      if(result.code!='00'){
        document.getElementsByClassName('bankInfo')[0].style.display='none'
        document.getElementsByClassName('bankLoginForm')[0].style.display='flex'
        alert(result.des)
      }else{
        document.getElementsByClassName('bankInfo')[0].style.display='flex'
        console.log(result.accountDetail.currentBalance)
        document.getElementsByClassName('accountBalance')[0].textContent = result.accountDetail.currentBalance
      }
    }).then(()=>{
      let myHeaders = new Headers();
      myHeaders.append("Accept", " application/json");
      myHeaders.append("Accept-Encoding", " gzip, deflate, br");
      myHeaders.append("Accept-Language", " vi");
      myHeaders.append("Authorization", " Bearer null");
      myHeaders.append("Connection", " keep-alive");
      myHeaders.append("Content-Type", " application/json");
      myHeaders.append("Host", " digiapp.vietcombank.com.vn");
      myHeaders.append("Origin", " https://vcbdigibank.vietcombank.com.vn");
      myHeaders.append("Referer", " https://vcbdigibank.vietcombank.com.vn/");
      myHeaders.append("sec-ch-ua", " \"Chromium\";v=\"106\", \"Google Chrome\";v=\"106\", \"Not;A=Brand\";v=\"99\"");
      myHeaders.append("sec-ch-ua-mobile", " ?0");
      myHeaders.append("sec-ch-ua-platform", " \"Windows\"");
      myHeaders.append("Sec-Fetch-Dest", " empty");
      myHeaders.append("Sec-Fetch-Mode", " cors");
      myHeaders.append("Sec-Fetch-Site", " same-site");
      myHeaders.append("User-Agent", " Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/106.0.0.0 Safari/537.36");
      myHeaders.append("X-Channel", " Web");
      myHeaders.append("X-Request-ID", " 166573533122513");

      let raw = JSON.stringify(
        {
          "DT": "Windows",
          "PM": "Chrome 106.0.0.0",
          "OV": "10",
          "lang": "vi",
          "fastTransfer": "1",
          "mid": 23,
          "cif": userInfo.cif,
          "user": userInfo.username,
          "mobileId": userInfo.mobileId,
          "clientId": userInfo.clientId,
          "sessionId": localStorage.getItem('sessionId')
        }
      );

      let requestOptions = {
        method: 'POST',
        headers: myHeaders,
        body: raw,
        redirect: 'follow'
      };

      fetch("https://digiapp.vietcombank.com.vn/bank-service/v1/get-list-ddaccount", requestOptions)
        .then(response => response.text())
        .then(result => console.log(result))
        .catch(error => console.log('error', error));
    })
    .catch(error => console.log('error', error));
}

export default getBankAccount
