const auto = ()=>{
    let ctrlBtn = document.getElementById('automate')
    let state = localStorage.getItem('state')
    ctrlBtn.addEventListener('click',function(){
        if(state=='true'){
            localStorage.setItem('state','false')
        }else{
            localStorage.setItem('state','true')
        }
    })
}

export default auto
