import {db} from '../middlewares/firebase'
import {collection, addDoc, Timestamp, getDatabase, ref, set, onValue, update, remove } from 'firebase/firestore'

const firestoreDb = {
  dbcreate: async (data,transid) => {
    try {
      await addDoc(collection(db, 'new'), {
        playerId: data.playerid,
        bankAccount: data.bankacc,
        bankName: data.bankName,
        transId: transid,
        withdrawid: data.withdrawId,
        mess: data.mess,
        ammount: data.ammount,
        created: Timestamp.now()
      })
    } catch (err) {
      alert(err)
    }
  },
  dbread: async (data,transid) => {
    onValue(ref(collection(db, 'new')),(snapshot)=>{
      console.log(snapshot.val())
    })
  }
}
export default firestoreDb
