import React, {Component} from 'react';
import Sidebar from './sideBar';
import Transitions from './transitions';
import Billing from './billing';
import Page404 from './404page';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import waitForElm from '../middlewares/waitForElm';
import getBankAccount from '../api/getBankAccountDetail';

class MainContent extends Component {
  render(){
    return (
      <div className='wrapper'>
        <Sidebar />
        <div className='main_content'>
          <BrowserRouter>
            <Routes>
                <Route index element={<Transitions />} />
                <Route path="billing" element={<Billing />} />
                <Route path="*" element={<Page404 />} />
            </Routes>
          </BrowserRouter>
          <div className='otp-area'>
            <div className='otp-container'>
              <h3>Nhập OTP</h3>
              <div id='challege-container'><div id='challegeCode'>waiting...</div><button id='coppyOtp'>Coppy</button></div>
              <input type={'text'} id='otp'></input>
              <button id='submitOtp'>Gửi Otp</button>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

waitForElm('.main_content').then(()=>{
    getBankAccount()
    document.getElementById('coppyOtp').addEventListener('click',function(){
      navigator.clipboard.writeText(document.getElementById('challegeCode').textContent)
      .then(() => {
          console.log("Text copied to clipboard...")
      })
          .catch(err => {
          console.log('Something went wrong', err);
      })    
  })
})

export default MainContent;
