import React, {Component} from 'react';
import Bill from './bill.generator';
import Dbread from '../controllers/readData.controllers';
import waitForElm from '../middlewares/waitForElm';
import HistorySearch from './history.formField';

class Billing extends Component{
  render(){
    return (
      <div className='billing'>
        <div className='breadCrum'><a href='#'>Trang Chủ</a> - <a href='#'>VCB</a> - <a href='#'>Chuyển Khoản</a></div>
        <h2 className='pageTitle'>Hóa Đơn Chuyển Khoản</h2>
        <HistorySearch />
        {/* <Dbread /> */}
        <table className='transHistory' cellSpacing={0}>
        <thead>
          <th>Player Id</th>
          <th>Số Tài Khoản</th>
          <th>Ngân Hàng</th>
          <th>Số Tiền</th>
          <th>Tên Khách Hàng</th>
          <th>Mã Xuất Khoản</th>
          <th>Mã Giao Dịch</th>
          <th>Tạo Hóa Đơn</th>
          <th></th>
        </thead>
        <tbody className='tableData'> 

        </tbody>
      </table>
      <Bill />
      </div>
    )
  }
}

window.addEventListener('load', () => {
  var now = new Date();
  now.setMinutes(now.getMinutes() - now.getTimezoneOffset());

  /* remove second/millisecond if needed - credit ref. https://stackoverflow.com/questions/24468518/html5-input-datetime-local-default-value-of-today-and-current-time#comment112871765_60884408 */
  now.setMilliseconds(null)
  now.setSeconds(null)

  document.getElementById('startTime').value = now.toISOString().slice(0, -1);
});

waitForElm('.billPrinter').then(()=>{
  alert('hi')
  let day = ['Chủ Nhật','Thứ Hai','Thứ Ba','Thứ Tư','Thứ Năm','Thứ Sáu','Thứ Bảy',]
  let billPrnt = document.querySelectorAll('.billPrinter')
  let billingContent = document.getElementsByClassName('bills-container')[0]
  billPrnt.forEach((el)=>{
    el.addEventListener('click',()=>{
      console.log('bbbbbbbbb')
      let jsonpars = JSON.parse(el.getAttribute('data-id'))
      let decriptions = document.getElementsByClassName('decriptions')[0]
      let tdRight = document.getElementsByClassName('td-right')
      let time = new Date(jsonpars.created.seconds*1000)
      billingContent.style.opacity='1'
      billingContent.style.zIndex='10'
      decriptions.getElementsByTagName('h1')[0].textContent=jsonpars.ammount.toLocaleString()
      decriptions.getElementsByTagName('p')[0].textContent=time.getHours()+':'+time.getMinutes()+" "+day[time.getDay()]+" "+time.getDate()+"/"+(time.getMonth()+1)+"/"+time.getFullYear()
      tdRight[0].textContent=jsonpars.mess
      tdRight[1].textContent=jsonpars.bankAccount
      tdRight[2].textContent=jsonpars.bankName
      tdRight[3].textContent=jsonpars.transId
      tdRight[4].textContent=jsonpars.mess
    })
  })
  document.getElementsByClassName('cancelPrnt')[0].addEventListener('click',function(){
    console.log('center')
    billingContent.style.opacity='0'
    billingContent.style.zIndex='-1'
  })
})

export default Billing;
