import dateValue from '../constant/date'
var transitionID = []
const bankLogin = (user,password,captcha)=>{
    let myHeaders = new Headers();
    myHeaders.append("Accept", " application/json");
    myHeaders.append("Accept-Language", " vi");
    myHeaders.append("Content-Type", " application/json");
    myHeaders.append("Referer", " https://vcbdigibank.vietcombank.com.vn/");
    myHeaders.append("sec-ch-ua", " \"Chromium\";v=\"106\", \"Google Chrome\";v=\"106\", \"Not;A=Brand\";v=\"99\"");
    myHeaders.append("sec-ch-ua-mobile", " ?0");
    myHeaders.append("sec-ch-ua-platform", " \"Windows\"");
    myHeaders.append("User-Agent", " Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/106.0.0.0 Safari/537.36");
    myHeaders.append("X-Channel", " Web");
    myHeaders.append("X-Request-ID", " 166563358677041");
    myHeaders.append("Cookie", "TS01388157=011fc56c7656302927913af31e4c717c14b49aaad8a16d79f0d08b6139fa3f6eb236b6ab95ced8701036f05518b20a012bb9ebc7df");
    
    let raw = JSON.stringify(
        {
            "DT": "Windows",
            "PM": "Chrome 106.0.0.0",
            "OV": "10",
            "lang": "vi",
            "user": user,
            "password": password,
            "captchaToken": "a7f6fff3-22a6-e718-8ff8-ce3da2e463a8",
            "captchaValue": captcha,
            "checkAcctPkg": "1",
            "mid": 6
          }
    );
    
    let requestOptions = {
      method: 'POST',
      headers: myHeaders,
      body: raw,
      redirect: 'follow'
    };
    
    fetch("https://digiapp.vietcombank.com.vn/authen-service/v1/login", requestOptions)
      .then(response => response.json())
      .then(result => {
        localStorage.setItem('sessionId',result.sessionId)
        localStorage.setItem('userInfo',JSON.stringify(result.userInfo))
        console.log(result)
        if(result.code!='00'){
          document.getElementsByClassNam('bankInfo')[0].style.display='none'
            alert(result.des)
        }
    })
      .catch(error => console.log('error', error));
}

export default bankLogin
