const getDevice = ()=>{
    var token = localStorage.getItem('bankToken')
    var requestOptions = {
        method: 'GET',
        redirect: 'follow'
      };

      fetch("http://localhost:8090/TotalControl/v2/devices/main?token="+token, requestOptions)
        .then(response => response.json())
        .then(result => {
            console.log(result.id)
            if(!result.id){
                document.getElementsByClassName('bankLogin')[0].style.display="flex"
                console.log('Vui lòng đăng nhập')
            }
        })
        .catch(error => console.log('error', error));
}

export default getDevice
