const page1 = (data)=>{
    let token = localStorage.getItem('bankToken')
    let device = localStorage.getItem('deviceName')
    let requestOptions = {
        method: 'POST',
        redirect: 'follow'
      };
      
      fetch("http://localhost:8090/TotalControl/v2/devices/"+device+"/screen/texts?token="+token+"&text="+data.ammount+"&position=0", requestOptions)
        .then(response => response.json())
        .then(result => {
          let token = localStorage.getItem('bankToken')
          let device = localStorage.getItem('deviceName')
          let requestOptions = {
              method: 'POST',
              redirect: 'follow'
            };
            
            fetch("http://localhost:8090/TotalControl/v2/devices/"+device+"/screen/texts?token="+token+"&text="+data.mess+"&position=1", requestOptions)
              .then(response => response.json())
              .then(result => {
                  let requestOptions = {
                    method: 'POST',
                    redirect: 'follow'
                  };

                  fetch("http://localhost:8090/TotalControl/v2/devices/device@1144651618/sendAai?token=J6vgY3528IwFUEv1&params={query:'T:Chọn tài khoản',action:\"click\"}", requestOptions)
                    .then(response => response.text())
                    .then(result => console.log(result)).catch(error => console.log('error', error));
              }).catch(error => console.log('error', error));
        }).catch(error => console.log('error', error));
        //Set amount and message
}

export default page1
