const getHistory = (startTime, endTime, playerChoices, playerId)=>{
    let url = []
    let myHeaders = new Headers();
    myHeaders.append("accept", " */*");
    myHeaders.append("accept-encoding", " gzip, deflate, br");
    myHeaders.append("authorization", localStorage.getItem('token'));
    myHeaders.append("origin", " https://bo.jun88.bet");
    myHeaders.append("referer", " https://bo.jun88.bet/");
    myHeaders.append("Cookie", "__cf_bm=eRN8pUNPav1TR9mYCn51uVAev2F2uFlVAz.7869Bo.k-1665682812-0-AZouNqKJFI125f1jlLoD4aT09ABUKzvjNDQa0JJDpyr9Rn7rmSa4oQvWG6kLxuRUj9neqrot9qSSAA1QSrJ6NIs=");

    let requestOptions = {
    method: 'GET',
    headers: myHeaders,
    redirect: 'follow'
    };

    if(playerId==''){
      url[0]='https://boapi.jun88.bet/jun88-ims/api/v1/withdrawals/search?&starttime='+startTime+'&endtime='+endTime+'&dl=false&exactmatch=true&limit=25&sort=DESC&sortcolumn=withdrawaltime&statusType=WITHDRAWAL_RECORD&timefilter=withdrawaltime&zoneType=ASIA_SHANGHAI&auditor=jun88996'
    }
    
    if(playerId!=''){
      if(playerChoices=='playerId'){
        url[0]='https://boapi.jun88.bet/jun88-ims/api/v1/withdrawals/search?&starttime='+startTime+'&endtime='+endTime+'&dl=false&exactmatch=true&limit=25&sort=DESC&sortcolumn=withdrawaltime&statusType=WITHDRAWAL_RECORD&timefilter=withdrawaltime&zoneType=ASIA_SHANGHAI&searchType=playerid&search='+playerId
      }
    }

    fetch(url[0], requestOptions)
    .then(response => response.json())
    .then(result=> {
        document.getElementsByClassName('tableData')[0].innerHTML=''
        result.data.forEach(element => {
            console.log(element)
            let dataTable = document.getElementsByClassName('tableData')[0]
            let tableTr = document.createElement('tr')
            dataTable.appendChild(tableTr)
            let playerId = document.createElement('td')
            let playerIdCont = document.createTextNode(element.playerid)
            playerId.appendChild(playerIdCont)
            tableTr.appendChild(playerId)
    
            let bankAcc = document.createElement('td')
            let bankAccNum = document.createTextNode(element.bankaccount.slice(2))
            bankAcc.appendChild(bankAccNum)
            tableTr.appendChild(bankAcc)
    
            let bank = document.createElement('td')
            let bankName = document.createTextNode(element.bankname)
            bank.appendChild(bankName)
            tableTr.appendChild(bank)
    
            let amount = document.createElement('td')
            let amountNum = document.createTextNode(element.actualwithdrawalamt)
            amount.appendChild(amountNum)
            tableTr.appendChild(amount)
    
            let name = document.createElement('td')
            let nameValue = document.createTextNode(element.bankaccountname)
            name.appendChild(nameValue)
            tableTr.appendChild(name)
    
            let withdraw = document.createElement('td')
            let withdrawId = document.createTextNode(element.withdrawid)
            withdraw.appendChild(withdrawId)
            tableTr.appendChild(withdraw)
    
            let trans = document.createElement('td')
            let transId = document.createTextNode(element.approvereason)
            trans.appendChild(transId)
            tableTr.appendChild(trans)
    
            let btn = document.createElement('td')
            btn.innerHTML = '<button className="billPrinter" data-id={"withdrawid":"'+element.withdrawid+'","playerId":"'+element.playerid+'","bankAcc":"'+element.bankaccount+'","bankName":"'+element.bankname+'","ammount":'+element.actualwithdrawalamt*1000+',"mess":"'+element.bankaccountname+'","transId":"'+element.approvereason+'"}>PRINT</button>'
            tableTr.appendChild(btn)

            let day = ['Chủ Nhật','Thứ Hai','Thứ Ba','Thứ Tư','Thứ Năm','Thứ Sáu','Thứ Bảy',]
            let billPrnt = document.querySelectorAll('.billPrinter')
            let billingContent = document.getElementsByClassName('bills-container')[0]
            billPrnt.forEach((el)=>{
              el.addEventListener('click',()=>{
                console.log('aaaaaaaa')
                let jsonpars = JSON.parse(el.getAttribute('data-id'))
                let decriptions = document.getElementsByClassName('decriptions')[0]
                let tdRight = document.getElementsByClassName('td-right')
                let time = new Date(jsonpars.created.seconds*1000)
                billingContent.style.opacity='1'
                billingContent.style.zIndex='10'
                decriptions.getElementsByTagName('h1')[0].textContent=jsonpars.ammount.toLocaleString()
                decriptions.getElementsByTagName('p')[0].textContent=time.getHours()+':'+time.getMinutes()+" "+day[time.getDay()]+" "+time.getDate()+"/"+(time.getMonth()+1)+"/"+time.getFullYear()
                tdRight[0].textContent=jsonpars.mess
                tdRight[1].textContent=jsonpars.bankAccount
                tdRight[2].textContent=jsonpars.bankName
                tdRight[3].textContent=jsonpars.transId
                tdRight[4].textContent=jsonpars.mess
              })
            })
    
        });
    })
    .catch(error => console.log('error', error));
}

export default getHistory
