import {db} from './firebase'
import {collection, addDoc, Timestamp} from 'firebase/firestore'

const handleSubmit = async () => {
    try {
      await addDoc(collection(db, 'test'), {
        title: 'a',
        description: 'b',
        completed: 'c',
        created: Timestamp.now()
      })
      onClose()
    } catch (err) {
      alert(err)
    }
}

export default handleSubmit
